      this.FGColor = FGColor;
      this.BGColor = BGColor;
      this.BGColorAuto = false;
    }

    public ColorSetting( string Name, uint FGColor )
    {
      this.Name = Name;
      this.FGColor = FGColor;
      this.BGColorAuto = true;
    }

