  public class TokenInfo
  {
    public enum TokenType
    {
      UNKNOWN,
      BLANK,
      LABEL_GLOBAL,
      LABEL_LOCAL,
      OPCODE,
      OPERATOR,
      LITERAL_NUMBER,
      LITERAL_CHAR,
      LITERAL_STRING,
      LITERAL_DATA,     // a "string" with direct bytes to insert
      COMMENT,
      PSEUDO_OP,
      SEPARATOR,
      LABEL_INTERNAL,
      CALL_MACRO,
      COMMENT_IF_FIRST_CHAR,
      OPCODE_FIXED_ZP,
      OPCODE_FIXED_NON_ZP,
      OPCODE_DIRECT_VALUE,
      LABEL_CHEAP_LOCAL,
      LITERAL_REAL_NUMBER,
      SINGLE_CHAR,
      MACRO_PARAMETER
    }

    public string     OriginatingString;
    public int        StartPos = 0;
    public int        Length = 0;
    public TokenType  Type = TokenType.UNKNOWN;

    private string _Content = null;

    public string Content
    {
      get
      {
        if ( _Content != null )
        {
          return _Content;
        }
        return OriginatingString.Substring( StartPos, Length );
      }

      set
      {
        _Content = value;
      }
    }

    public int EndPos
    {
      get
      {
        return StartPos + Length - 1;
      }
    }
  };



