    {
      return AddKeyInfo( machine, Key, Modifier, Type, ScreenCodeValue, HasScreenCode, PetSCIIValue, HasPetSCII, CharValue, LowerCaseCharValue, LowerCasePETSCII, true, "" + (char)PetSCIIValue, "" );
    }



    static SingleKeyInfo AddKeyInfo( MachineType machine, PhysicalKey Key, KeyModifier Modifier, KeyType Type, byte ScreenCodeValue, bool HasScreenCode, byte PetSCIIValue, bool HasPetSCII, char CharValue, bool HasChar )
    {
      return AddKeyInfo( machine, Key, Modifier, Type, ScreenCodeValue, HasScreenCode, PetSCIIValue, HasPetSCII, CharValue, -1, 0, HasChar, "" + CharValue, "" );
    }



    static SingleKeyInfo AddKeyInfo( MachineType machine, PhysicalKey Key, KeyModifier Modifier, KeyType Type, byte ScreenCodeValue, bool HasScreenCode, byte PetSCIIValue, bool HasPetSCII, char CharValue, bool HasChar, string Desc )
    {
      return AddKeyInfo( machine, Key, Modifier, Type, ScreenCodeValue, HasScreenCode, PetSCIIValue, HasPetSCII, CharValue, -1, 0, HasChar, Desc, "" );
    }



    static SingleKeyInfo AddKeyInfo( MachineType machine, PhysicalKey Key, KeyModifier Modifier, KeyType Type, byte ScreenCodeValue, bool HasScreenCode, byte PetSCIIValue, bool HasPetSCII, char CharValue, int LowerCaseCharValue, byte LowerCasePETSCII, bool HasChar, string Desc, string ShortDesc )
    {
      var c64Char = new SingleKeyInfo( Key, ScreenCodeValue, HasScreenCode, PetSCIIValue, HasPetSCII, CharValue, HasChar, Desc, ShortDesc );
      c64Char.Modifier = Modifier;
      c64Char.Type = Type;
      c64Char.LowerCaseNative = LowerCasePETSCII;

      if ( c64Char.HasChar )
      {
        if ( ( c64Char.CharValue & 0xff00 ) == 0xee00 )
        {
          c64Char.LowerCaseDisplayChar = (char)( 0x100 + c64Char.CharValue );
        }
        else if ( ( c64Char.CharValue >= 'A' )
        &&        ( c64Char.CharValue <= 'Z' ) )
        {
          c64Char.LowerCaseDisplayChar = (char)( 0xef00 + ( c64Char.CharValue - 'A' + 1 ) );
        }
        else
        {
          c64Char.LowerCaseDisplayChar = c64Char.CharValue;
        }
      }

      if ( !AllPhysicalKeyInfos.ContainsKey( machine ) )
      {
        AllPhysicalKeyInfos.Add( machine, new List<SingleKeyInfo>() );
      }
      var allC64keys = AllPhysicalKeyInfos[machine];
      allC64keys.Add( c64Char );

      if ( !PhysicalKeyInfo.ContainsKey( machine ) )
      {
        PhysicalKeyInfo.Add( machine, new Map<PhysicalKey, KeyInfo>() );
      }
      var c64keys = PhysicalKeyInfo[machine];

      if ( !c64keys.ContainsKey( Key ) )
      {
        c64keys.Add( Key, new KeyInfo() );
      }

      if ( Modifier == KeyModifier.NORMAL )
      {
        c64keys[Key].Keys[KeyModifier.NORMAL] = c64Char;
      }
      if ( Modifier == KeyModifier.SHIFT )
      {
        c64keys[Key].Keys[KeyModifier.SHIFT] = c64Char;
      }
      if ( Modifier == KeyModifier.COMMODORE )
      {
        c64keys[Key].Keys[KeyModifier.COMMODORE] = c64Char;
      }
      if ( Modifier == KeyModifier.CONTROL )
      {
        c64keys[Key].Keys[KeyModifier.CONTROL] = c64Char;
      }



      if ( HasScreenCode )
      {
        if ( !ScreenCodeToChar.ContainsKey( ScreenCodeValue ) )
        {
          ScreenCodeToChar[ScreenCodeValue] = c64Char;
        }
      }
      if ( HasPetSCII )
      {
        if ( !PetSCIIToChar.ContainsKey( PetSCIIValue ) )
        {
          PetSCIIToChar[PetSCIIValue] = c64Char;
        }
      }
      if ( HasChar )
      {
        if ( !CharToC64Char.ContainsKey( CharValue ) )
        {
          CharToC64Char[CharValue] = c64Char;
        }
        if ( LowerCaseCharValue != -1 )
        {
          LowerCaseCharTo64Char[(char)LowerCaseCharValue] = c64Char;
        }
      }
      if ( ( HasPetSCII )
      &&   ( HasChar ) )
      {
        if ( !PETSCIIToUnicode.ContainsKey( PetSCIIValue ) )
        {
          PETSCIIToUnicode[PetSCIIValue] = CharValue;
        }
      }
      return c64Char;
    }



    public static SingleKeyInfo FindC64KeyByUnicode( char UnicodeValue )
    {
      foreach ( var c64Key in ConstantData.PhysicalKeyInfo[MachineType.C64] )
      {
        if ( ( c64Key.Value.Keys.ContainsKey( KeyModifier.SHIFT ) )
        &&   ( c64Key.Value.Keys[KeyModifier.SHIFT].CharValue == UnicodeValue ) )
        {
          return c64Key.Value.Keys[KeyModifier.SHIFT];
        }
        if ( ( c64Key.Value.Keys.ContainsKey( KeyModifier.CONTROL ) )
        &&   ( c64Key.Value.Keys[KeyModifier.CONTROL].CharValue == UnicodeValue ) )
        {
          return c64Key.Value.Keys[KeyModifier.CONTROL];
        }
        if ( ( c64Key.Value.Keys.ContainsKey( KeyModifier.COMMODORE ) )
        &&   ( c64Key.Value.Keys[KeyModifier.COMMODORE].CharValue == UnicodeValue ) )
        {
          return c64Key.Value.Keys[KeyModifier.COMMODORE];
        }
        if ( ( c64Key.Value.Keys.ContainsKey( KeyModifier.NORMAL ) )
        &&   ( c64Key.Value.Keys[KeyModifier.NORMAL].CharValue == UnicodeValue ) )
        {
          return c64Key.Value.Keys[KeyModifier.NORMAL];
        }
      }
      return null;
    }



    public static SingleKeyInfo FindC64KeyByPETSCII( byte PETSCIIValue )
    {
      // Codes 192-223 map to Codes  96-127
      // Codes 224-254 map to Codes 160-190
      // Code  255     map to Code  126
      if ( ( PETSCIIValue >= 192 )
      &&   ( PETSCIIValue <= 223 ) )
      {
        PETSCIIValue -= 192 - 96;
      }
      else if ( ( PETSCIIValue >= 224 )
      &&        ( PETSCIIValue <= 254 ) )
      {
        PETSCIIValue -= 224 - 160;
      }
      else if ( PETSCIIValue == 255 )
      {
        PETSCIIValue = 126;
      }
      foreach ( var c64Key in ConstantData.AllPhysicalKeyInfos[MachineType.C64] )
      {
        if ( c64Key.NativeValue == PETSCIIValue )
        {
          return c64Key;
        }
      }
      return null;
    }

